// Wiki Base URL für Bilder
const WIKI_IMAGE_BASE = 'https://hellokittyislandadventure.wiki.gg/images'

// Helper um Item-Namen zu Wiki-Bildnamen zu konvertieren
const wikiImage = (name) => `${WIKI_IMAGE_BASE}/${name.replace(/ /g, '_')}.png`

let itemId = 1
const createItem = (name, nameDE, category, subcategory, rarity, tags = []) => ({
  id: String(itemId++),
  name,
  nameDE,
  category,
  subcategory,
  rarity,
  tags,
  imageUrl: wikiImage(name),
})

